import React from 'react'
import styled from 'styled-components'

function Header() {
    return (
        <Nav>
            <Logo src="/images/logo.svg" />
            <NavMenu>
                <a>
                    <img src="/images/home-icon.svg" />
                    <span>HOME</span>
                </a>

                <a>
                    <img src="/images/search-icon.svg" />
                    <span>SEARCH</span>
                </a>

                <a>
                    <img src="/images/watchlist-icon.svg" />
                    <span>WATCHLIST</span>
                </a>

                <a>
                    <img src="/images/original-icon.svg" />
                    <span>ORIGINALS</span>
                </a>

                <a>
                    <img src="/images/movie-icon.svg" />
                    <span>MOVIES</span>
                </a>

                <a>
                    <img src="/images/series-icon.svg" />
                    <span>SERIES</span>
                </a>

            </NavMenu>

            <UserImg src= " " />

        </Nav>
    )
}

export default Header

const Nav = styled.nav`
    height: 70px;
    background: #090b13;
    display: flex;
    align-items: center;
    padding: 0 36px; //0 arriba y abajo 36 a cada lado 

`

const Logo = styled.img`
    width: 80px;

`

const NavMenu = styled.div`
    display: flex;
    flex: 1; //dice que el navmenu es lo más importante y que lo expandas lo max posible por lo tanto lleva a la der del todo a la img
    margin-left: 20px;
    a {
        display: flex;
        align-items: center;
        padding: 0 12px;

        img {
            height: 20px;
        }

        span {
            font-size: 13px;
            letter-spacing: 1.42px;
        }

    }
`

const UserImg = styled.img`
    width: 48px;
    height: 48px;
    boder-radius: 50%;
    cursor: pointer;


`
